import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

import Button from "../../shared/components/FormElements/Button";
import Input from "../../shared/components/FormElements/Input";
import {
  VALIDATOR_REQUIRE,
  VALIDATOR_MINLENGTH,
} from "../../shared/util/validators";
import "./NewPlace.css";
import { useForm } from "../../shared/hooks/form-hook";

const DUMMY_PLACES = [
  {
    id: "p1",
    title: "SVNIT- surat",
    description: "One of the reputed NIT's in surat",
    imageUrl:
      "https://upload.wikimedia.org/wikipedia/commons/thumb/b/b6/Image_created_with_a_mobile_phone.png/1200px-Image_created_with_a_mobile_phone.png",
    address: "chchhanath Surat- Dumas, Road, Keval Chowk, Surat",
    location: {
      lat: 21.1640345,
      long: 72.7782708,
    },
    creator: "u1",
  },
  {
    id: "p2",
    title: "SVNIT- surat",
    description: "One of the reputed NIT's in surat",
    imageUrl:
      "https://static.toiimg.com/thumb/msid-68192064,width-1070,height-580,imgsize-126695,resizemode-75,overlay-toi_sw,pt-32,y_pad-40/photo.jpg",
    address: "chchhanath Surat- Dumas, Road, Keval Chowk, Surat",
    location: {
      lat: 21.1640345,
      long: 72.7782708,
    },
    creator: "u2",
  },
];

const UpdatePlace = () => {
  const [isLoading, setIsLoading] = useState(true);
  const placeId = useParams().placeId;

  const [formstate, inputHandler, setFormData] = useForm(
    {
      title: {
        value: "",
        isValid: false,
      },
      description: {
        value: "",
        isValid: false,
      },
    },
    false
  );

  const identifyPlace = DUMMY_PLACES.find((p) => p.id === placeId);

  useEffect(() => {
    if (identifyPlace) {
      setFormData(
        {
          title: {
            value: identifyPlace.title,
            isValid: true,
          },
          description: {
            value: identifyPlace.description,
            isValid: true,
          },
        },
        true
      );
    }
    setIsLoading(false);
  }, [setFormData, identifyPlace]);

  if (!identifyPlace) {
    return (
      <div className="center">
        <h2>Couldn't find place! </h2>
      </div>
    );
  }

  const placeUpdateSubmitHandler = (event) => {
    event.preventDefault();
    console.log(formstate.inputs);
  };

  if (isLoading) {
    return (
      <div className="center">
        <h2>Loading</h2>
      </div>
    );
  }

  return (
    <form className="place-form" onSubmit={placeUpdateSubmitHandler}>
      <Input
        id="title"
        element="input"
        type="text"
        label="Title"
        validators={[VALIDATOR_REQUIRE()]}
        errorText="Please enter a valid title"
        onInput={inputHandler}
        initialValue={formstate.inputs.title.value}
        initialValid={formstate.inputs.title.isValid}
      />
      <Input
        id="description"
        element="textarea"
        label="Description"
        validators={[VALIDATOR_MINLENGTH(5)]}
        errorText="Please enter a valid description (atleast 5 character)"
        onInput={inputHandler}
        initialValue={formstate.inputs.description.value}
        initialValid={formstate.inputs.description.isValid}
      />
      <Button type="submit" disabled={!formstate.isValid}>
        Update Place
      </Button>
    </form>
  );
};

export default UpdatePlace;
